import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { Camera, Play, Square, Trophy, TrendingUp, CheckCircle, AlertCircle, Heart, RefreshCw } from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://localhost:5000';

function App() {
  const [isStarted, setIsStarted] = useState(false);
  const [selectedExercise, setSelectedExercise] = useState('squat');
  const [repCount, setRepCount] = useState(0);
  const [formScore, setFormScore] = useState(100);
  const [feedback, setFeedback] = useState('Ready to go bananas? üçå');
  const [totalBananas, setTotalBananas] = useState(0);
  const [streak, setStreak] = useState(0);
  const [cameraError, setCameraError] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('disconnected');

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const animationRef = useRef(null);

  const exercises = [
    { 
      id: 'squat', 
      name: 'Squats', 
      icon: 'ü¶µ', 
      tips: 'Keep chest up, knees behind toes',
      description: 'Lower your body by bending knees to 90¬∞'
    },
    { 
      id: 'pushup', 
      name: 'Push-ups', 
      icon: 'üí™', 
      tips: 'Straight back, elbows at 45¬∞',
      description: 'Lower chest to ground, keep core tight'
    },
    { 
      id: 'plank', 
      name: 'Plank', 
      icon: 'üèãÔ∏è', 
      tips: 'Straight line from head to heels',
      description: 'Hold position, engage core and glutes'
    },
    { 
      id: 'curl', 
      name: 'Bicep Curls', 
      icon: 'üí™', 
      tips: 'Keep elbows stationary, control the motion',
      description: 'Curl weights up, squeeze biceps at top'
    }
  ];

  useEffect(() => {
    checkConnection();
  }, []);

  const checkConnection = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/health`);
      if (response.data.status === 'healthy') {
        setConnectionStatus('connected');
      }
    } catch (error) {
      setConnectionStatus('disconnected');
      console.error('Backend connection failed:', error);
    }
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: 640, 
          height: 480,
          facingMode: 'user'
        } 
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        streamRef.current = stream;
      }
      setCameraError('');

      videoRef.current.onloadedmetadata = () => {
        processVideoFrame();
      };
    } catch (err) {
      setCameraError('Camera access denied. Please enable camera permissions.');
      console.error('Camera error:', err);
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const processVideoFrame = async () => {
    if (!videoRef.current || !canvasRef.current || !isStarted) {
      return;
    }

    const canvas = canvasRef.current;
    const video = videoRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    try {
      canvas.toBlob(async (blob) => {
        if (blob && isStarted) {
          const formData = new FormData();
          formData.append('frame', blob, 'frame.jpg');
          formData.append('exercise', selectedExercise);

          try {
            const response = await axios.post(`${API_BASE_URL}/process_frame`, formData, {
              headers: { 'Content-Type': 'multipart/form-data' }
            });

            if (response.data) {
              if (response.data.rep_count !== undefined) {
                setRepCount(response.data.rep_count);
              }
              if (response.data.form_score !== undefined) {
                setFormScore(response.data.form_score);
              }
              if (response.data.feedback) {
                setFeedback(response.data.feedback);
              }
              if (response.data.annotated_frame) {
                const img = new Image();
                img.onload = () => {
                  ctx.drawImage(img, 0, 0);
                };
                img.src = `data:image/jpeg;base64,${response.data.annotated_frame}`;
              }
            }
          } catch (error) {
            console.error('Frame processing error:', error);
          }
        }

        if (isStarted) {
          animationRef.current = requestAnimationFrame(processVideoFrame);
        }
      }, 'image/jpeg', 0.8);
    } catch (error) {
      console.error('Canvas error:', error);
    }
  };

  const handleStart = async () => {
    setIsStarted(true);
    setRepCount(0);
    setFormScore(100);
    setFeedback('Get ready to train! üçå');

    try {
      await axios.post(`${API_BASE_URL}/start_session`, {
        exercise: selectedExercise
      });
      await startCamera();
    } catch (error) {
      console.error('Failed to start session:', error);
      setFeedback('Failed to connect to backend üò¢');
    }
  };

  const handleStop = async () => {
    setIsStarted(false);
    stopCamera();

    try {
      const response = await axios.post(`${API_BASE_URL}/end_session`);
      if (response.data) {
        setTotalBananas(prev => prev + Math.floor(repCount / 5));
        if (response.data.streak) {
          setStreak(response.data.streak);
        }
      }
      setFeedback('Great workout! You earned it! üçå');
    } catch (error) {
      console.error('Failed to end session:', error);
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <div className="header-content">
          <div className="logo-section">
            <div className="banana-icon">üçå</div>
            <div>
              <h1>NanaFit</h1>
              <p className="tagline">Go Bananas with Perfect Form!</p>
            </div>
          </div>
          <div className="stats-row">
            <div className="stat-card">
              <div className="stat-icon-value">
                <Trophy className="stat-icon trophy" />
                <span className="stat-number">{totalBananas}</span>
              </div>
              <div className="stat-label">Bananas Earned</div>
            </div>
            <div className="stat-card">
              <div className="stat-icon-value">
                <TrendingUp className="stat-icon streak" />
                <span className="stat-number">{streak}</span>
              </div>
              <div className="stat-label">Day Streak</div>
            </div>
            <div className={`connection-status ${connectionStatus}`}>
              <div className="status-dot"></div>
              {connectionStatus === 'connected' ? 'Backend Connected' : 'Backend Offline'}
            </div>
          </div>
        </div>
      </header>

      <main className="main-content">
        <div className="content-grid">
          <div className="video-section">
            <div className="video-container">
              <div className="video-wrapper">
                {!isStarted ? (
                  <div className="video-placeholder">
                    <div className="placeholder-content">
                      <div className="big-banana">üçå</div>
                      <p className="placeholder-title">Ready to start your workout?</p>
                      <p className="placeholder-subtitle">Select an exercise and press Start</p>
                    </div>
                  </div>
                ) : (
                  <>
                    <video
                      ref={videoRef}
                      autoPlay
                      playsInline
                      muted
                      className="video-feed"
                    />
                    <canvas
                      ref={canvasRef}
                      className="video-overlay"
                    />
                    {cameraError && (
                      <div className="error-overlay">
                        <div className="error-content">
                          <AlertCircle className="error-icon" />
                          <p>{cameraError}</p>
                        </div>
                      </div>
                    )}
                  </>
                )}
              </div>

              <div className="feedback-bar">
                <div className="feedback-stats">
                  <div className="rep-counter">
                    <div className="counter-number">{repCount}</div>
                    <div className="counter-info">
                      <div className="counter-label">Reps</div>
                      <div className="counter-sublabel">Keep pushing!</div>
                    </div>
                  </div>
                  <div className="form-score">
                    <div className="score-info">
                      <div className="score-label">Form Score</div>
                      <div className="score-sublabel">Looking good!</div>
                    </div>
                    <div className="score-number">{formScore}%</div>
                  </div>
                </div>
                <div className="feedback-message">
                  {formScore >= 90 ? (
                    <CheckCircle className="feedback-icon good" />
                  ) : (
                    <AlertCircle className="feedback-icon warning" />
                  )}
                  <p>{feedback}</p>
                </div>
              </div>
            </div>
          </div>

          <div className="control-panel">
            <div className="panel-card">
              <h2 className="panel-title">
                <Heart className="title-icon" />
                Choose Exercise
              </h2>
              <div className="exercise-list">
                {exercises.map((ex) => (
                  <button
                    key={ex.id}
                    onClick={() => setSelectedExercise(ex.id)}
                    disabled={isStarted}
                    className={`exercise-card ${selectedExercise === ex.id ? 'selected' : ''} ${isStarted ? 'disabled' : ''}`}
                  >
                    <div className="exercise-header">
                      <span className="exercise-icon">{ex.icon}</span>
                      <span className="exercise-name">{ex.name}</span>
                    </div>
                    <p className="exercise-tips">{ex.tips}</p>
                    <p className="exercise-description">{ex.description}</p>
                  </button>
                ))}
              </div>
            </div>

            <div className="panel-card">
              <div className="control-buttons">
                {!isStarted ? (
                  <button
                    onClick={handleStart}
                    className="control-btn start-btn"
                    disabled={connectionStatus !== 'connected'}
                  >
                    <Play className="btn-icon" />
                    Start Workout
                  </button>
                ) : (
                  <button
                    onClick={handleStop}
                    className="control-btn stop-btn"
                  >
                    <Square className="btn-icon" />
                    Stop Workout
                  </button>
                )}
                <button
                  onClick={checkConnection}
                  className="control-btn refresh-btn"
                  disabled={isStarted}
                >
                  <RefreshCw className="btn-icon" />
                  Check Connection
                </button>
              </div>
            </div>

            <div className="tips-card">
              <h3 className="tips-title">üçå Beginner Tips</h3>
              <ul className="tips-list">
                <li>
                  <span className="tip-bullet">‚óè</span>
                  <span>Position yourself so your full body is visible</span>
                </li>
                <li>
                  <span className="tip-bullet">‚óè</span>
                  <span>Move slowly and focus on form over speed</span>
                </li>
                <li>
                  <span className="tip-bullet">‚óè</span>
                  <span>Listen to the feedback and adjust your posture</span>
                </li>
                <li>
                  <span className="tip-bullet">‚óè</span>
                  <span>Earn bananas by completing sets with good form!</span>
                </li>
                <li>
                  <span className="tip-bullet">‚óè</span>
                  <span>Make sure your backend server is running on port 5000</span>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
