import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import { motion } from "framer-motion";

function App() {
  const [repCount, setRepCount] = useState(0);
  const [streak, setStreak] = useState(3);
  const [bananas, setBananas] = useState(12);
  const videoRef = useRef(null);

  useEffect(() => {
    // Access webcam
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        if (videoRef.current) videoRef.current.srcObject = stream;
      })
      .catch((err) => console.error("Camera error:", err));
  }, []);

  return (
    <div className="app-container">
      {/* Top-left overlay */}
      <motion.div
        className="hud"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="hud-item">
          üçå Bananas: <span>{bananas}</span>
        </div>
        <div className="hud-item">
          üî• Streak: <span>{streak}</span>
        </div>
      </motion.div>

      {/* Main content with split screen */}
      <div className="main-screen">
        {/* AI Banana Man */}
        <motion.div
          className="banana-box"
          initial={{ scale: 0.9 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.6, yoyo: Infinity }}
        >
          <img
            src="https://cdn-icons-png.flaticon.com/512/415/415733.png"
            alt="Banana Man"
            className="banana-image"
          />
          <p className="banana-text">Follow the Banana Man‚Äôs moves!</p>
        </motion.div>

        {/* Camera feed */}
        <motion.div
          className="camera-box"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          <video ref={videoRef} autoPlay playsInline muted className="camera-feed" />
        </motion.div>
      </div>

      {/* Bottom control panel */}
      <motion.div
        className="control-panel"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <button className="banana-button">üçå Start Workout</button>
        <button className="banana-button">üéµ Play Music</button>
        <button className="banana-button">üíæ Save Progress</button>
      </motion.div>
    </div>
  );
}

export default App;
