import React, { useRef, useEffect } from "react";
import { motion } from "framer-motion";
import { Play, Pause, Camera, RefreshCw } from "lucide-react";

export default function App() {
  const videoRef = useRef(null);

  // Start the user's camera
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error("Error accessing camera:", error);
      }
    };
    startCamera();
  }, []);

  return (
    <div className="flex flex-col h-screen bg-gradient-to-b from-yellow-100 via-yellow-50 to-white overflow-hidden">
      {/* Main Split Screen */}
      <div className="flex flex-1 gap-4 p-4">
        {/* Left: AI Banana Man */}
        <motion.div
          className="flex-1 bg-yellow-200 rounded-3xl shadow-lg flex flex-col justify-center items-center overflow-hidden border-4 border-yellow-300"
          initial={{ opacity: 0, x: -100 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <motion.img
            src="https://media.giphy.com/media/v1.Y2lkPTc5MGI3NjExOXhwb2ZnMTdtdXB4NmQ5OHlkM2RkZDA5bXZhdDV2d29nbDBvY3g4MCZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/W5m0XWQzQZ8jGvJrZ9/giphy.gif"
            alt="AI Banana Man"
            className="w-64 h-64 rounded-full shadow-md"
            animate={{ rotate: [0, 10, -10, 0] }}
            transition={{ repeat: Infinity, duration: 4 }}
          />
          <p className="text-2xl mt-4 font-bold text-yellow-800">
            üçå AI Banana Man: Let‚Äôs move!
          </p>
        </motion.div>

        {/* Right: User Camera */}
        <motion.div
          className="flex-1 bg-gray-100 rounded-3xl shadow-lg flex justify-center items-center border-4 border-gray-200"
          initial={{ opacity: 0, x: 100 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <video
            ref={videoRef}
            autoPlay
            playsInline
            className="w-full h-full object-cover rounded-3xl"
          ></video>
        </motion.div>
      </div>

      {/* Bottom Control Panel */}
      <motion.div
        className="bg-white rounded-t-3xl shadow-2xl flex justify-center items-center gap-8 py-4 border-t-4 border-yellow-300"
        initial={{ y: 100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        {/* Start Button */}
        <motion.button
          whileTap={{ scale: 0.9 }}
          className="bg-yellow-400 hover:bg-yellow-500 text-white font-semibold px-6 py-2 rounded-full shadow-lg flex items-center gap-2"
        >
          <Play size={20} /> Start
        </motion.button>

        {/* Pause Button */}
        <motion.button
          whileTap={{ scale: 0.9 }}
          className="bg-red-400 hover:bg-red-500 text-white font-semibold px-6 py-2 rounded-full shadow-lg flex items-center gap-2"
        >
          <Pause size={20} /> Pause
        </motion.button>

        {/* Capture Pose Button */}
        <motion.button
          whileTap={{ scale: 0.9 }}
          className="bg-green-400 hover:bg-green-500 text-white font-semibold px-6 py-2 rounded-full shadow-lg flex items-center gap-2"
        >
          <Camera size={20} /> Capture Pose
        </motion.button>

        {/* Reset Button */}
        <motion.button
          whileTap={{ scale: 0.9 }}
          className="bg-blue-400 hover:bg-blue-500 text-white font-semibold px-6 py-2 rounded-full shadow-lg flex items-center gap-2"
        >
          <RefreshCw size={20} /> Reset
        </motion.button>
      </motion.div>
    </div>
  );
}
