            </div>

            <div className="exercises-grid">
              {selectedMuscleGroup?.exercises.map((exercise, index) => (
                <button
                  key={exercise.id}
                  className="exercise-card"
                  onClick={() => handleExerciseClick(exercise)}
                  style={{ '--exercise-color': selectedMuscleGroup.color }}
